import React, { Component } from 'react';
import './App.css';
import FormUpload from './components/FormUpload';
import socketIOClient from 'socket.io-client';
const internalIp = require('internal-ip');

class App extends Component {

  state = {
    files: [],
    endpoint: `http://${internalIp.v4.sync()}`
  }

  componentDidMount = () => {
    (async () => {
      console.log(await internalIp.v4());
      //=> '10.0.0.79'
    })();

    console.log(internalIp.v4.sync())

    const socket = socketIOClient(this.state.endpoint);
    socket.on('reloadFilesList', (data) => {
      this.setState({ files: data })
    })
  }

  componentWillMount() {
    fetch(`${this.state.endpoint}:8000/files`, {
      method: 'POST',
    }).then((response, error) => {
      response.json().then((body) => {
        this.setState({ files: body.files });
      }).catch(err => {
        console.log('caught it!', err);
      });
    });
  }

  render() {
    const files = this.state.files.map(file => <p>{file}</p>);

    return (
      <div className="App">
        <FormUpload endpoint={this.state.endpoint} />
        <div className="filesList">
          <h2>Upload files successfully ( {files.length} )</h2>
          {files}
        </div>
      </div>
    );
  }
}

export default App;
